puts 'Starting on invoices'

@start_date = '2012-01-01'.to_date
@end_date = @start_date + 30

def change_dates
  @start_date += 30
  @end_date += 30
end

def create_invoices(start_date, end_date, customer)
  num = Invoice.next_number(@account)
  puts "Invoice number: #{num}"
  @invoice = Invoice.create!(date: end_date, 
                          comment: "bills since #{start_date}",
                          account_id: @account.id, 
                          customer_id: customer.id,
                          number: num)
  bills = @account.bills.where("date > ? AND date < ? AND customer_id = ?", start_date, end_date, customer.id)
  bills.each do |bill|
    bill.update_attribute(:invoice_id, @invoice.id)
  end
end 

until Bill.count - Bill.count(:invoice_id) < 30
  create_invoices(@start_date, @end_date, @home)
  create_invoices(@start_date, @end_date, @business)
  puts "From #{@start_date} to #{@end_date}"
  change_dates
end

puts 'finished invoices'



